function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),t=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),t.classList.add("locked"))})),s.addEventListener("click",(i=>{i.target&&(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav);const swiper=new Swiper(".info__swiper",{slidesPerView:1,spaceBetween:20,autoplay:{delay:4e3},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}});